/* Progress bar animation */
.elementor-11 .elementor-element.elementor-element-9e16fcf .elementor-progress-bar {
   animation: progressAnimation 15s linear infinite;  /* Use linear for constant speed */
}

@keyframes progressAnimation {
   0% {
       width: 0%;
   }
   10% {
       width: 10%;
   }
   25% {
       width: 25%;
   }
   33% {
       width: 33%;
   }
   50% {
       width: 50%;
   }
   66% {
       width: 66%;
   }
   75% {
       width: 75%;
   }
   90% {
       width: 90%;
   }
   100% {
       width: 100%;
   }
}

/* Default text color for the target element */
.elementor-button-text {
   color: #00AB9E; /* Default color */
}

/* Changing color (this will be triggered by JavaScript) */
